export const PageContentDocument = gql`
    query PageContent($uri: String = "") {
  nodeByUri(uri: $uri) {
    ... on Page {
      id
      title
      date
      editorBlocks {
        ...CommonBlockFields
      }
    }
    ... on Post {
      id
      title
      date
      featuredImage {
        node {
          altText
          mediaDetails {
            sizes {
              name
              mimeType
              height
              width
              sourceUrl
            }
          }
        }
      }
      editorBlocks {
        ...CommonBlockFields
      }
    }
  }
}
    ${CommonBlockFieldsFragmentDoc}`;
